import time

class Timer:
    def __init__(self):
        self.starting_time = None
        self.ending_time = None
        self.elapsed = None

    def start(self):
        self.starting_time = time.perf_counter()

    def end(self):
        self.ending_time = time.perf_counter()
        self.elapsed = self.ending_time - self.starting_time

    def __enter__(self):
        self.start()

    def __exit__(self, *args):
        self.end()

    def formatted(self, elapsed, fmt):
        if fmt:
            return fmr % f
        return elapsed

    def s(self, fmt=None):
        return self.formatted(self.elapsed, fmt)

    def ms(self, fmt=None):
        return self.formatted(self.elapsed * 1000, fmt)

    def ns(self, fmt=None):
        return self.formatted(self.elapsed * 1000 * 1000, fmt)

    def current(self):
        if not self.starting_time:
            return None
        return time.perf_counter() - self.starting_time
